fn heapify(arr: &mut [i32], mut idx: usize, size: usize) { while idx < size { let left_idx = idx * 2 + 1; let right_idx = left_idx + 1; if left_idx < size && right_idx < size { // 左右子树都在范围内的情况 if arr[left_idx] < arr[right_idx] { // 左子树小于右子树 if arr[right_idx] > arr[idx] { // 右子树最大 arr.swap(right_idx, idx); idx = right_idx; } else { return; } } else if arr[left_idx] > arr[idx] { // 左子树最大 arr.swap(left_idx, idx); idx = left_idx; } else { // 当前值最大，直接返回 return; } } else if left_idx < size { // 左子树在范围内 if arr[left_idx] > arr[idx] { // 左子树最大 arr.swap(left_idx, idx); idx = left_idx; } else { // 当前值最大，直接返回 return; } } else { // 都不在范围内直接返回 return; } }
